	render() {
		return (
			<div>
				<Header />
				<Switch>
					<Route exact path='/' component={HomePage} />
					<Route path='/shop' component={ShopPage} />
					<Route
						exact
						path='/registration'
						component={RegistrationAuthenticationPage}
					/>
				</Switch>
			</div>
		);
	}
}

export default App;
